INSERT INTO habilidade_racas (raca_id, nome, descricao, opcional, nivel_minimo) VALUES
((SELECT id FROM racas WHERE nome = 'Humano'), 'Versátil', 'Você se torna treinado em duas perícias a sua escolha (não precisam ser da sua classe). Você pode trocar uma dessas perícias por um poder geral a sua escolha.', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Anão'), 'Conhecimento das Rochas', 'Você recebe visão no escuro e +2 em testes de Percepção e Sobrevivência realizados no subterrâneo.', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Anão'), 'Devagar e Sempre', 'Seu deslocamento é 6m (em vez de 9m). Porém, seu deslocamento não é reduzido por uso de armadura ou excesso de carga.', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Anão'), 'Duro como Pedra', 'Você recebe +3 pontos de vida no 1º nível e +1 por nível seguinte.', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Anão'), 'Tradição de Heredrimm', 'Para você, todos os machados, martelos, marretas e picaretas são armas simples. Você recebe +2 em ataques com essas armas.', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Dahllan'), 'Amiga das Plantas', 'Você pode lançar a magia Controlar Plantas (atributo-chave Sabedoria). Caso aprenda novamente essa magia, seu custo diminui em -1 PM.', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Dahllan'), 'Armadura de Allihanna', 'Você pode gastar uma ação de movimento e 1 PM para transformar sua pele em casca de árvore, recebendo +2 na Defesa até o fim da cena.', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Dahllan'), 'Empatia Selvagem', 'Você pode se comunicar com animais por meio de linguagem corporal e vocalizações. Você pode usar Adestramento para mudar atitude e persuasão com animais. Caso receba esta habilidade novamente, recebe +2 em Adestramento.', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Elfo'), 'Graça de Glórienn', 'Seu deslocamento é 12m (em vez de 9m).', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Elfo'), 'Sangue Mágico', 'Você recebe +1 ponto de mana por nível.', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Elfo'), 'Sentidos Élficos', 'Você recebe visão na penumbra e +2 em Misticismo e Percepção.', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Goblin'), 'Engenhoso', 'Você não sofre penalidades em testes de perícia por não usar ferramentas. Se usar a ferramenta necessária, recebe +2 no teste de perícia.', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Goblin'), 'Espelunqueiro', 'Você recebe visão no escuro e deslocamento de escalada igual ao seu deslocamento terrestre.', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Goblin'), 'Peste Esguia', 'Seu tamanho é Pequeno, mas seu deslocamento se mantém 9m. Apesar de pequenos, goblins são rápidos.', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Goblin'), 'Rato das Ruas', 'Você recebe +2 em Fortitude e sua recuperação de PV e PM nunca é inferior ao seu nível.', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Lefou'), 'Cria da Tormenta', 'Você é uma criatura do tipo monstro e recebe +5 em testes de resistência contra efeitos causados por lefeu e pela Tormenta.', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Lefou'), 'Deformidade', 'Você recebe +2 em duas perícias a sua escolha. Cada um desses bônus conta como um poder da Tormenta (exceto para perda de Carisma). Você pode trocar um desses bônus por um poder da Tormenta a sua escolha (ele também não conta para perda de Carisma).', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Minotauro'), 'Chifres', 'Você possui uma arma natural de chifres (dano 1d6, crítico x2, perfuração). Uma vez por rodada, quando usa a ação agredir para atacar com outra arma, pode gastar 1 PM para fazer um ataque corpo a corpo extra com os chifres.', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Minotauro'), 'Couro Rígido', 'Sua pele é dura como a de um touro. Você recebe +1 na Defesa.', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Minotauro'), 'Faro', 'Você tem olfato apurado. Contra inimigos em alcance curto que não possa perceber, você não fica desprevenido e camuflagem total lhe causa apenas 20% de chance de falha.', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Minotauro'), 'Medo de Altura', 'Se estiver adjacente a uma queda de 3m ou mais de altura (como um buraco ou penhasco), você fica abalado.', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Qareen'), 'Desejos', 'Se lançar uma magia que alguém tenha pedido desde seu último turno, o custo da magia diminui em -1 PM. Fazer um desejo ao qareen é uma ação livre.', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Qareen'), 'Resistência Elemental', 'Conforme sua ascendência, você recebe redução 10 a um tipo de dano. Escolha uma: frio (qareen da água), eletricidade (do ar), fogo (do fogo), ácido (da terra), luz (da luz) ou trevas (qareen das trevas).', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Qareen'), 'Tatuagem Mística', 'Você pode lançar uma magia de 1º círculo a sua escolha (atributo-chave Carisma). Caso aprenda novamente essa magia, seu custo diminui em -1 PM.', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Golem'), 'Chassi', 'Seu corpo artificial é resistente, mas rígido. Seu deslocamento é 6m, mas não é reduzido por uso de armadura ou excesso de carga. Você recebe +2 na Defesa, mas possui penalidade de armadura -2. Você leva um dia para vestir ou remover uma armadura. Sua armadura não conta no limite de itens que você pode usar.', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Golem'), 'Criatura Artificial', 'Você é uma criatura do tipo construto. Recebe visão no escuro e imunidade a efeitos de cansaço, metabólicos e de veneno. Não precisa respirar, alimentar-se ou dormir. Precisa ficar inerte por oito horas por dia para recarregar. Cura não funciona em você, mas Ofício (artesão) pode ser usada no lugar.', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Golem'), 'Fonte Elemental', 'Você possui um espírito elemental preso em seu corpo. Escolha entre água (frio), ar (eletricidade), fogo (fogo) e terra (ácido). Você é imune a dano desse tipo. Se fosse sofrer dano mágico desse tipo, em vez disso cura PV em quantidade igual à metade do dano.', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Golem'), 'Propósito de Criação', 'Você foi construído para um propósito específico e não teve uma infância. Você não tem direito a escolher uma origem, mas recebe um poder geral a sua escolha.', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Hynne'), 'Arremessador', 'Quando faz um ataque à distância com uma funda ou uma arma de arremesso, seu dano aumenta em um passo.', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Hynne'), 'Pequeno e Rechonchudo', 'Seu tamanho é Pequeno e seu deslocamento é 6m. Você recebe +2 em Enganação e pode usar Destreza como atributo-chave de Atletismo (em vez de Força).', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Hynne'), 'Sorte Salvadora', 'Quando faz um teste de resistência, você pode gastar 1 PM para rolar este teste novamente.', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Kliren'), 'Híbrido', 'Sua natureza multifacetada fez com que você aprendesse conhecimentos variados. Você se torna treinado em uma perícia a sua escolha (não precisa ser da sua classe).', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Kliren'), 'Engenhosidade', 'Quando faz um teste de perícia, você pode gastar 2 PM para somar sua Inteligência no teste. Você não pode usar esta habilidade em testes de ataque. Caso receba esta habilidade novamente, seu custo é reduzido em -1 PM.', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Kliren'), 'Ossos Frágeis', 'Você sofre 1 ponto de dano adicional por dado de dano de impacto.', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Kliren'), 'Vanguardista', 'Você recebe proficiência em armas de fogo e +2 em Ofício (um qualquer, a sua escolha).', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Medusa'), 'Cria de Megalokk', 'Você é uma criatura do tipo monstro e recebe visão no escuro.', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Medusa'), 'Natureza Venenosa', 'Você recebe resistência a veneno +5 e pode gastar uma ação de movimento e 1 PM para envenenar uma arma que esteja usando. A arma causa perda de 1d12 pontos de vida. O veneno dura até você acertar um ataque ou até o fim da cena.', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Medusa'), 'Olhar Atordoante', 'Você pode gastar uma ação de movimento e 1 PM para forçar uma criatura em alcance curto a fazer um teste de Fortitude (CD Car). Se a criatura falhar, fica atordoada por uma rodada (apenas uma vez por cena).', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Osteon'), 'Armadura Óssea', 'Você recebe redução de corte, frio e perfuração 5.', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Osteon'), 'Memória Póstuma', 'Você se torna treinado em uma perícia ou recebe um poder geral a sua escolha. Como alternativa, pode ser um osteon de outra raça humanoide, ganhando uma habilidade dessa raça.', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Osteon'), 'Natureza Esquelética', 'Você é um morto-vivo. Recebe visão no escuro e imunidade a efeitos de cansaço, metabólicos, de trevas e de veneno. Não precisa respirar, alimentar-se ou dormir. Cura de luz causa dano e dano de trevas recupera seus PV.', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Osteon'), 'Preço da Não Vida', 'Você precisa passar oito horas sob a luz de estrelas ou no subterrâneo para recuperar PV e PM por descanso.', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Sereia/Tritão'), 'Canção dos Mares', 'Você pode lançar duas das magias a seguir: Amedrontar, Comando, Despedaçar, Enfeitiçar, Hipnotismo ou Sono (atributo-chave Carisma). Caso aprenda novamente uma dessas magias, seu custo diminui em -1 PM.', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Sereia/Tritão'), 'Mestre do Tridente', 'Para você, o tridente é uma arma simples. Além disso, você recebe +2 em rolagens de dano com azagaias, lanças e tridentes.', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Sereia/Tritão'), 'Transformação Anfíbia', 'Você pode respirar debaixo d''água e possui uma cauda (natação 12m). Fora d''água, sua cauda vira pernas (deslocamento 9m). Se ficar mais de um dia sem contato com água, não recupera PM com descanso.', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Sílfide'), 'Asas de Borboleta', 'Seu tamanho é Minúsculo. Você pode pairar a 1,5m do chão com deslocamento 9m. Pode gastar 1 PM por rodada para voar com deslocamento de 12m.', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Sílfide'), 'Espírito da Natureza', 'Você é uma criatura do tipo espírito, recebe visão na penumbra e pode falar com animais livremente.', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Sílfide'), 'Magia das Fadas', 'Você pode lançar duas das magias a seguir (atributo-chave Carisma): Criar Ilusão, Enfeitiçar, Luz e Sono. Caso aprenda novamente uma dessas magias, seu custo diminui em -1 PM.', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Suraggel (Aggelus)'), 'Herança Divina', 'Você é uma criatura do tipo espírito e recebe visão no escuro.', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Suraggel (Aggelus)'), 'Luz Sagrada', 'Você recebe +2 em Diplomacia e Intuição. Além disso, pode lançar Luz (como uma magia divina: atributo-chave Carisma). Caso aprenda novamente essa magia, seu custo diminui em -1 PM.', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Suraggel (Sulfure)'), 'Herança Divina', 'Você é uma criatura do tipo espírito e recebe visão no escuro.', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Suraggel (Sulfure)'), 'Sombras Profanas', 'Você recebe +2 em Enganação e Furtividade. Além disso, pode lançar Escuridão (como uma magia divina: atributo-chave Inteligência). Caso aprenda novamente essa magia, seu custo diminui em -1 PM.', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Trog'), 'Mau Cheiro', 'Você pode gastar uma ação padrão e 2 PM para expelir um gás fétido. Todas as criaturas (exceto trogs) em alcance curto devem passar em um teste de Fortitude contra veneno (CD Con) ou ficarão enjoadas durante 1d6 rodadas.', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Trog'), 'Mordida', 'Você possui uma arma natural de mordida (dano 1d6, crítico x2, perfuração). Uma vez por rodada, quando usa a ação agredir para atacar com outra arma, pode gastar 1 PM para fazer um ataque corpo a corpo extra com a mordida.', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Trog'), 'Reptiliano', 'Você é uma criatura do tipo monstro e recebe visão no escuro, +1 na Defesa e, se estiver sem armadura ou roupas pesadas, +5 em Furtividade.', FALSE, 1),
((SELECT id FROM racas WHERE nome = 'Trog'), 'Sangue Frio', 'Você sofre 1 ponto de dano adicional por dado de dano de frio.', FALSE, 1);
